mapper.update:

	UPDATE AB_DOC SET
		IMG_SRC=#{SRC, jdbcType=VARCHAR}, IMG_SRC_SIZ=#{SRC_SIZ}
	WHERE
		ID=#{ID} AND SEQ=#{SEQ}


	UPDATE AB_DOC SET
		IMG_RSLT=#{SRC, jdbcType=VARCHAR}, IMG_RSLT_SIZ=#{SRC_SIZ}
	WHERE
		ID=#{ID} AND SEQ=#{SEQ}


	UPDATE AB_DOC SET
		THUMB_WID=#{WID}, THUMB_HGT=#{HGT},
		THUMB_SRC=#{SRC, jdbcType=VARCHAR}, THUMB_SRC_SIZ=#{SRC_SIZ}
	WHERE
		ID=#{ID} AND SEQ=#{SEQ}

mapper.select:

	SELECT IMG_SRC_SIZ LENGTH, IMG_SRC BYTES
	FROM AB_DOC
	WHERE ID=#{ID} AND SEQ=#{SEQ}


	SELECT IMG_RSLT_SRC LENGTH, IMG_RSLT BYTES
	FROM AB_DOC
	WHERE ID=#{ID} AND SEQ=#{SEQ}


	SELECT THUMB_SRC_SIZ LENGTH, THUMB_SRC BYTES
	FROM AB_DOC
	WHERE ID=#{ID} AND SEQ=#{SEQ}
